import styled from 'styled-components';

export const Name = styled.p`
  font-weight: 500;
`;

export const Inner = styled.div`
  display: flex;
  margin-right: 0;
  gap: 8px;
`;

export const Remove = styled.button`
  background-color: transparent;
  border: none;
  color: red;
  cursor: pointer;
`;
